import React, { useEffect, useRef } from "react";
import { useActions } from "../hooks/useActions";
import { useTypedSelector } from "../hooks/useTypedSelector";
import './SearchContainer.css'

const SearchContainer = () => {

    const {setRequesTitle, getFetchImages} = useActions()
    const {requestTitle} = useTypedSelector (state => state.images)

    useEffect( () => {
        getFetchImages(requestTitle)
    }, [])

    const title = useRef<HTMLInputElement>(null)
    function setTitle (e: React.ChangeEvent<HTMLInputElement>) {
        setRequesTitle(String(title.current?.value))
    }

    function getImages () {
        getFetchImages(requestTitle)
    }

    function search(e: any) {                                           
       if (e.keyCode == 13) getFetchImages(requestTitle)
    }

    return (
        <div 
        className="inputContainer">
            <input 
            ref={title}
            onChange={ (e) => setTitle(e)}
            onKeyDown={ (e) => search(e)}
            type='text'
            className="textInputField"
            placeholder="Яблоко" /> 
            <button 
            onClick={ (e) => getImages()}
            className="getFetch"> Искать </button>
        </div>

    )
}

export default SearchContainer
